import React from "react";


function Items({items}){


    const mappedItems = items.map((item) => {
        return(
            <React.Fragment key={item.id}>
                <h2>{item.name}</h2>
                <p>{item.description}</p>
                <p>{item.price}</p>
            </React.Fragment>
        )
    })

    return (
        <div>
            <h1>Items</h1>
            {mappedItems}
        </div>
    )
}

export default Items;
